    LDPLAYER_PATH = r"C:\leidian\LDPlayer9\ldconsole.exe"

    @classmethod
    def get_service_account_file(cls):
        key_bytes = base64.b64decode(cls.SERVICE_ACCOUNT_B64)
        tmp = tempfile.NamedTemporaryFile(delete=False, suffix=".json")
        tmp.write(key_bytes)
        tmp.close()
        return tmp.name